text][/vc_column][vc_column width=\"1/3\"][vc_custom_heading text=\"Our Solution\" google_fonts=\"font_family:Roboto%3A100%2C100italic%2C300%2C300italic%2Cregular%2Citalic%2C500%2C500italic%2C700%2C700italic%2C900%2C900italic|font_style:400%20regular%3A400%3Anormal\"][vc_empty_space height=\"9px\"][vc_column_text][simple-accordian][/vc_column_text][/vc_column][vc_column width=\"1/3\"][vc_custom_heading text=\"Our Expertise\" google_fonts=\"font_family:Roboto%3A100%2C100italic%2C300%2C300italic%2Cregular%2Citalic%2C500%2C500italic%2C700%2C700italic%2C900%2C900italic|font_style:400%20regular%3A400%3Anormal\"][vc_empty_space height=\"9px\"][media image=\"2161\"][/vc_column][/vc_row]', 'Our Institute', '', 'inherit', 'closed', 'closed', '', '1974-revision-v1', '', '', '2018-12-17 10:59:42', '2018-12-17 10:59:42', '', 1974, 'http://139.59.83.25/~fvowerid/acs/2018/12/17/1974-revision-v1/', 0, 'revision', '', 0),
